import Input from "./Input";
import Button from "./Button.js";
import {useState} from 'react';

function AuthModal() {

    const [modalType, setModalType] = useState('login');
    return (
        <div className="w-screen h-screen fixed top-0 left-0 z-20 flex" style={{backgroundColor:'rgba(0,0,0,.6)'}}>
            <div className="border border-biome_dark-brightest w-3/4 sm:w-1/2 lg:w-1/4 bg-biome_dark p-5 text-biome_text self-center mx-auto rounded-md">
                {modalType === 'login' && (
                    <h1 className="text-2xl mb-5">Login</h1>
                )}
                {modalType === 'register' && (
                    <h1 className="text-2xl mb-5">Sign Up</h1>
                )}

                <label>
                    <span className="text-biome_text-darker text-sm">Username:</span>
                    <Input type="text" className="mb-3 w-full rounded-md" placeholder="username"/>
                </label>

                <label>
                    <span className="text-biome_text-darker text-sm">Password:</span>
                    <Input type ="Password" className="mb-3 w-full" placeholder ="password"/>
                </label>

                <Button className="w-full py-2 mb-3" style={{borderRadius:'.3rem'}}>Log In</Button>
                {modalType === 'login' && (
                <div>
                    New to Biome? <button className="text-blue-600" onClick={() => setModalType('register')}>Sign Up</button>
                </div>
                )}
                 {modalType === 'register' && (
                <div>
                    Already have an account? <button onClick={() => setModalType('login')}>Sign Up</button>
                </div>
                )}
            </div>
        </div>  
    );
}
export default AuthModal;
