import './styles.css';
import Logo from './biomelogo.png';
import Avatar from './biomeavatar.png';
import Button from './Button.js';
import {BellIcon, ChatIcon, SearchIcon, PlusIcon, ChevronDownIcon, UserIcon, LoginIcon} from '@heroicons/react/outline';
import {useState} from 'react';
import React from 'react';
import ClickOutHandler from "react-clickout-handler"



function Header() {
  const [userDropdownVisibilityClass, setUserDropdownVisibilityClass] = useState('hidden');

  function toggleUserDropdown(){
    if (userDropdownVisibilityClass === 'hidden') {
      setUserDropdownVisibilityClass('block');
    } else {
      setUserDropdownVisibilityClass('hidden');
    }
  }

    return (
        <header className="flex w-full bg-biome_dark p-2">
          <div className="mx-4 flex relative">
            <img src={Logo} alt="" className="w-8 h-8 mr-4"/>
            <form action="" className="bg-biome_dark-brighter px-3 flex rounded-md border border-biome_border mx-4 flex-grow">
            <SearchIcon className="text-gray-300 h-6 w-6 mt-1"/>
            <input type="text" className="bg-biome_dark-brighter text-sm p-1 pl-2 pr-0 block focus:outline-none text-white" placeholder="Search"/>
          </form>

          {/* <button className="px-2 py-1">
              <ChatIcon className="text-gray-400 w-6 h-6 mx-2"/>
          </button>
          <button className="px-2 py-1">
              <BellIcon className="text-gray-400 h-6 mx-2"/>
          </button>
          <button className="px-2 py-1">
              <PlusIcon className="text-gray-400 w-6 h-6 mx-2"/>
          </button> */}

          <div className = "mx-2">  
            <Button outline className ="mr-1">Login</Button>
            <Button className =" ">Sign Up</Button>
          </div>


          
    
          
            <button className="rounded-md flex ml-4 border border-gray-700" onClick={() => toggleUserDropdown()}>
            <UserIcon className="w-6 h-6 text-gray-400 m-1" />
              {/* <div className=" bg-gray-600 rounded-md w-8 h-8"> */}
                {/* <img src={Avatar} alt="" className="block"/> */}
              {/* </div> */}
              <ChevronDownIcon className="text-gray-500 w-5 h-5 mt-2 m-1"/>
            </button>
         
          <div className={"absolute right-0 top-8 bg-biome_dark border border-gray-700 z-10 rounded-md text-biome_text overflow-hidden "+userDropdownVisibilityClass}>
              <button className="block flex w-50 py-2 px-3 hover:bg-gray-300 hover:text-black text-sm">
                <LoginIcon className="w-5 h-5 mr-2"/>
                Login/Signup
              </button>
            </div>
          </div>
      
        </header>
    );
}

export default Header;
